var t = debug.assert;

var a = "hello";

t(a == "hello");
t(a.length == 5);

var t = debug.assert;

var a = "hello" + " world";

t(a == "hello world");
t(a.length == 11);

a += '!';
t(a == "hello world!");
t(a.length == 12);

