test-case.script:
var t = debug.assert;

var arr = [1,2,3,4,5,6,7];

t( arr.length == 7 );
t( arr[0] == 1 );
t( arr[6] == 7 );

var t = debug.assert;

var arr = [];

arr.push(1);
arr.push(2, 3, 4);
arr.push(5, 6, 7);

t( arr.length == 7 );
t( arr[0] == 1 );
t( arr[6] == 7 );

var t = debug.assert;

var arr = [1,2,3,4,5];

arr[0] = 9;
t( arr[0] + arr[1] == 11 );

var t = debug.assert;

var arr = [1,2,3,4,5];

arr.splice(0,1);
t(arr.length==4);

arr.splice(1,1,10,11);
t(arr.length==5);

t(arr[0] == 2);
t(arr[1] == 10);
t(arr[2] == 11);
t(arr[3] == 4);
t(arr[4] == 5);

var t = debug.assert;

var arr = [1,2,3,4,5];

for(e in arr)
  if(e==3) arr.splice(2,1);

t( arr.length == 4 );
t( arr[0] == 1 );
t( arr[1] == 2 );
t( arr[2] == 4 );
t( arr[3] == 5 );

var t = debug.assert;

var a = 1, b = 2, c = 3;
var arr = [ a, b, c, b * c, a + c];

t( arr[0] == 1 );
t( arr[1] == 2 );
t( arr[2] == 3 );
t( arr[3] == 6 );
t( arr[4] == 4 );

arr[0] = function() { 
  return (a + b) * 2;
};

t( arr[0]() == 6 );

a = 15;
b = 5;
t( arr[0]() == 40 );
