var t = debug.assert;

var a = 10, b = 20;
if( a + b >= 30 )
  t( true );
else
  t( false );

var t = debug.assert;

function check(a) {
  if (a < 5) 
    return 20;
  else if (a < 10)
    return 30;
  else 
    return 40;
}

t( check(3) , 20 );
t( check(8) , 30 );
t( check(13), 40 );

var t = debug.assert;

function check(a) {
  if (a < 5) 
    if (a < 3)
      return 3;
    else 
      return 5;
  else 
    if (a < 10)
      return 10;
    else if (a < 20)
      return 20;
}

t( check(2) , 3 );
t( check(4) , 5 );
t( check(8) , 10 );
t( check(13), 20 );
t( check(23), null );

