var t = debug.assert;

var o=0;
for(var i=0;i<10;i++)
  o+=i;

t( o == 45 );

var t = debug.assert;

var o = '';

var s = 'hello world!';

for(var i=0;i<s.length;i++)
  if(s[i] != ' ' && s[i] != '!')
    o += s[i];

t( o == 'helloworld' );

